import React, { useEffect, useState, useRef } from 'react';
import { PlusOutlined } from '@ant-design/icons';
import { Image, Input, Upload, button } from 'antd';
import axios from 'axios';
import moment from 'moment';
import style from './ReviewPage.module.css';
import { useNavigate } from 'react-router-dom';
import Navbar from '../Navbar'

const getBase64 = (file) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });
}

const { TextArea } = Input;
export default function ReviewPage() {
    const navigate = useNavigate();
    // const text = useRef(new Map());
    const [commentData, setCommentData] = useState([])
    const [previewOpen, setPreviewOpen] = useState(false);
    const [previewImage, setPreviewImage] = useState('');
    const [fileList, setFileList] = useState([]);
    const handlePreview = async (file) => {
        if (!file.url && !file.preview) {
          file.preview = await getBase64(file.originFileObj);
        }
        setPreviewImage(file.url || file.preview);
        setPreviewOpen(true);
    };
    const handleChange = ({ fileList: newFileList }) => setFileList(newFileList);
    const uploadButton = (
        <button
          style={{
            border: 0,
            background: 'none',
          }}
          type="button"
        >
          <PlusOutlined />
          <div
            style={{
              marginTop: 8,
            }}
          >
            Upload
          </div>
        </button>
    );

    const handlePostComment = (id) => {
        // newCommentData.comment?.push(text.current.get(id))
        // 调用接口，为某个发布的image增加新的评论
        // axios.put(`/api/commentData/${id}`, {
        //     ...commentData,
        // }).then(res => {
        //     if(res.data){
        //         axios.get('/api/commentData').then(res => {
        //             // 获取评论数据,初始化
        //             if(res.data){
        //                 setCommentData(res.data)
        //             }
        //         })
        //     }
        // })
    }

    const handlePostImage = () => {
        // 判断照片是否上传成功，上传失败，给出错误提示，直接return

        // 照片上传成功，调用发布照片接口，发布照片成功后

        // 照片发布成功，刷新当前照片评论列表，可添加评论
        axios.post('/api/commentData', {
            "imglist": [
                "http://example.png",
                "http://example.jpg"
            ],
            "comment": [{
                "userid": localStorage.getItem('id'),
                "username": localStorage.getItem('username'),
                "comment": '',
                "createtime": moment().format('YYYY-MM-DD HH:mm:ss')
            }]
        }).then(res => {
            if(res.data){
                axios.get('/api/commentData').then(res => {
                    // 获取评论数据,初始化
                    if(res.data){
                        setCommentData(res.data)
                    }
                })
            }
        })
    }

    useEffect(() => {
        if(!localStorage.getItem('id')){
            navigate('/login')
        }
        axios.get('/api/commentData').then(res => {
            // 获取评论数据,初始化
            if(res.data){
                setCommentData(res.data)
            }
        })
    }, [])

    return <div>
        <h1>ReviewPage</h1>
        <Navbar/>
        <div className={style.commentWrapper}>
            {
                commentData?.map(item => {
                    return <div className={style.imgTopic}>
                        <div className={style.imglist}>
                            {
                                item.imglist?.map(el => {
                                    return <Image width={200} height={200} src={el.src} fallback="data:image/png;base64,"/>
                                })
                            }
                        </div>
                        <div className={style.comment}>
                            {
                                item.comment?.map(el => {
                                    return <div className={style.rowComment}>
                                        <p>{el.username} {el.createtime}</p>
                                        <p style={{marginTop: '8px'}}>{el.comment}</p>
                                    </div>
                                })
                            }
                        </div>
                        <div className={style.commentInput}>
                            <TextArea/>
                            <button onClick={handlePostComment(item.id)}>评论</button>
                        </div>
                    </div>
                })
            }
        </div>
        <div className={style.uploadWrapper}>
            <Upload
                width={200}
                height={200}
                style={{display:'flex', justifyContent:'center'}}
                // 照片上传地址需要修改成后端真实地址
                action="/api/upload"
                listType="picture-card"
                fileList={fileList}
                onPreview={handlePreview}
                onChange={handleChange}
                >
                {fileList.length >= 2 ? null : uploadButton}
            </Upload>
            {previewImage && (
                <Image
                    width={200}
                    height={200}
                    wrapperStyle={{
                        display: 'none',
                    }}
                    preview={{
                        visible: previewOpen,
                        onVisibleChange: (visible) => setPreviewOpen(visible),
                        afterOpenChange: (visible) => !visible && setPreviewImage(''),
                    }}
                    src={previewImage}
                />
            )}
        </div>
        <div style={{marginTop: '16px'}} onClick={handlePostImage}>
            <button type="primary">Post Photo</button>
        </div>
    </div>
}